(95, 1, '2020-07-01 05:58:20', '2020-07-01 02:58:20', 'a:15:{s:4:\"type\";s:5:\"image\";s:12:\"instructions\";s:0:\"\";s:8:\"required\";i:0;s:17:\"conditional_logic\";i:0;s:7:\"wrapper\";a:3:{s:5:\"width\";s:0:\"\";s:5:\"class\";s:0:\"\";s:2:\"id\";s:0:\"\";}s:13:\"return_format\";s:3:\"url\";s:12:\"preview_size\";s:6:\"medium\";s:7:\"library\";s:3:\"all\";s:9:\"min_width\";s:0:\"\";s:10:\"min_height\";s:0:\"\";s:8:\"min_size\";s:0:\"\";s:9:\"max_width\";s:0:\"\";s:10:\"max_height\";s:0:\"\";s:8:\"max_size\";s:0:\"\";s:10:\"mime_types\";s:0:\"\";}', 'Иконка для виджета', 'section_icon', 'publish', 'closed', 'closed', '', 'field_5efbfb9ba13d1', '', '', '2020-07-01 06:09:50', '2020-07-01 03:09:50', '', 47, 'http://flowers.local/?post_type=acf-field&#038;p=95', 3, 'acf-field', '', 0),
